function UserList({ users }) {
  // if (!users.length) return null;

  const renderedUsers = users.map(({ name, email }, i) => {
    return (
      <tr key={`table-row-${i}`}>
        <td>{name}</td>
        <td>{email}</td>
      </tr>
    );
  });

  return (
    <ul>
      {users.map(({ name, email }, i) => {
        return (
          <li key={`table-row-${i}`}>
            <span>{name}</span>
            <span>{email}</span>
          </li>
        );
      })}
    </ul>
  );

  return (
    <table data-testid='users-table'>
      <thead>
        <tr>
          <th>Name</th>
          <th>Email</th>
        </tr>
      </thead>
      <tbody data-testid='users-table-body'>
        {users.map(({ name, email }, i) => {
          return (
            <tr key={`table-row-${i}`}>
              <td>{name}</td>
              <td>{email}</td>
            </tr>
          );
        })}
      </tbody>
    </table>
  );
}

export default UserList;
