import { useState } from 'react';

function UserForm({ onUserAdd }) {
  const [values, setValues] = useState({
    name: '',
    email: '',
  });

  const onChange = (e) =>
    setValues((prev) => ({ ...prev, [e.target.name]: e.target.value }));

  const onSubmit = (e) => {
    e.preventDefault();
    onUserAdd(values);
  };
  return (
    <form onSubmit={onSubmit}>
      <div>
        <label>Name</label>

        <input
          name='name'
          type='text'
          onChange={onChange}
          value={values.name}
        />
      </div>
      <div>
        <label>Email</label>
        <input
          name='email'
          type='text'
          onChange={onChange}
          value={values.email}
        />
      </div>
      <button type='submit'>Add user</button>
    </form>
  );
}

export default UserForm;
